This sample shows how to read a zip file
            
            using System;
            using System.Text;
            using System.IO;
            
            using Vuforia.SharpZipLib.Zip;
            
            class MainClass
            {
            	public static void Main(string[] args)
            	{
            		using ( ZipInputStream s = new ZipInputStream(File.OpenRead(args[0]))) {
           
            			ZipEntry theEntry;
            			const int size = 2048;
